window.addEventListener("load",(()=>{const e=document.querySelector("header"),t=e.querySelector("h1"),o=e.querySelector("nav").querySelectorAll("a");t.textContent="Bienvenido a la Libreria",o.forEach((t=>{t.addEventListener("click",(o=>{o.preventDefault(),function(){const t=`#${Math.floor(16777215*Math.random()).toString(16)}`;e.style.backgroundColor=t}(),setTimeout((()=>{window.location.href=t.href}),300)}))}))}));